	public static boolean addProduct(Product product) {
		if (!product.accepted) {
			System.out.println("Product not yet accepted");
			return false;
		}
		else if (Product.exists(product)) {
			System.out.println("Product already exists");
			return false;
		}

		Product.addToDB(product);
		return true;
	}

